    this.ZERO_ADDR = '0x0000000000000000000000000000000000000000';
    this.web3 = new _web.default(_ChainConfig.CHAIN_RPC[chainId]);
    this.chainId = chainId;
    this.tokens = [];
    this.account = account;
    this.apiModules = [];
  }

  registerModule(module) {
    this.apiModules.push(module);
  }

  initModules() {
    for (let module of this.apiModules) {
      module.initialize();
    }

    for (let module of this.apiModules) {
      module.initAfter();
    }
  }

  getContract(abi, address) {
    if (!abi || !address) {
      throw 'Illegal getContract address:', address;
    }

    return new this.web3.eth.Contract(abi, address);
  }

  getContractMethods(abi, address) {
    return new this.web3.eth.Contract(abi, address).methods;
  }

  getContractAddr(name) {
    return _ChainConfig.ContractsAddr[this.chainId][name];
  }

  getTokenAddress(name) {
    return _ChainConfig.Tokens[this.chainId][name];
  }

  isZeroAddress(addr) {
    return addr == this.ZERO_ADDR;
  }

  getZeroSymbol() {
    return _ChainConfig.ChainSymbol.ZeroToken[this.chainId];
  }

  async getGasPrice() {
    return await this.web3.eth.getGasPrice();
  }

  async getBlockNumber() {
    let res = await this.web3.eth.getBlockNumber();
    return Number(res);
  }

  getBlockSpanTime() {
    if (this.chainId == 1) {
      return 13.5;
    }

    return 3;
  }

